INSERT INTO `psy_evaluation`.`scale` (`name`, `level`, `create_user`, `create_time`, `update_time`, `deleted`, `duration`, `introduction`, `conclusion`, `type`, `application`, `warning_options`, `crowd`) VALUES ('霍兰德职业兴趣测试量表', '2', '0', '2023-01-09 09:41:13', '2023-01-09 09:41:13', '0', 90, '请根据对每一题目的第一印象作答，不必仔细推敲，答案没有好坏、对错之分。根据自己的实际情况回答“是”或“否”。######霍兰德职业兴趣自测（Self-Directed Search）是由美国职业指导专家霍兰德 （John Holland）根据他本人大量的职业咨询经验及其职业类型理论编制的测评工具。 霍兰德认为，个人职业兴趣特性与职业之间应有一种内在的对应关系。 根据兴趣的不同，人格可分为研究型（I）、艺术型（A）、社会型（S）、企业型（E）、传统型（C）、现实型（R）六个维度，每个人的性格都是这六个维度的不同程度组合。\n该测评适用于需要发现和确定自己的职业兴趣和能力特长的所有人士。', '请根据对每一题目的第一印象作答，不必仔细推敲，答案没有好坏、对错之分。根据自己的实际情况回答“是”或“否”。', '1', '生涯规划', '0', '高中以上、教师');
